mapper.select:
select*from (
select ox.id,ox.question, null num1, null num2, null num3, null num4, ox.answer,"ox" category,ox.reg_date,ox.user_id, nvl(f.user_id=1,0) `like` from user_ox ox left join user_quizzes_favorite f on ox.id=f.user_ox_id UNION all
select multi.id,multi.question, num1,num2,num3,num4, multi.answer,"multi" category,multi.reg_date,multi.user_id , nvl(f.user_id=1,0) `like` from user_multiple_choice_quiz multi left join user_quizzes_favorite f on multi.id=f.user_multiple_choice_quiz_id
UNION all
select short.id,short.question, null num1, null num2, null num3, null num4, short.answer,"short" category,short.reg_date,short.user_id, nvl(f.user_id=1,0) `like` from user_short_answer_quiz short left join user_quizzes_favorite f on short.id=f.user_short_answer_quiz_id
) qv
qv.user_id=#{userId}
and qv.id=#{id}
and qv.category =#{cate}

select * from user_ox


     question LIKE '%${query}%'



         and user_id=#{userId}



         limit #{offset}, #{size}

select * from user_multiple_choice_quiz


     question LIKE '%${query}%'



         and user_id=#{userId}



         limit #{offset}, #{size}

select * from short_answer_quiz


    question LIKE '%${query}%'



        and user_id=#{userId}



        limit #{offset}, #{size}

select *from (select id,question, null num1, null num2, null num3, null num4, answer,"ox" category,reg_date,user_id from user_ox UNION all select id,question, num1,num2,num3,num4, answer,"multi" category,reg_date,user_id from user_multiple_choice_quiz UNION all select id,question, null num1, null num2, null num3, null num4, answer,"short" category,reg_date,user_id from user_short_answer_quiz) qv
question LIKE '%${query}%'
and qv.user_id=${userId}
limit #{offset}, #{size}
